/*global google*/

import logo from './logo.svg';
import './style.css';
import { GoogleMap } from '@googlemaps/map-loader'
import React from 'react';

//const google = window.google.

const mapOptions = {
  center: {
    lat: 47.649196,
    lng: -122.350384
  },
  zoom: 12
}

const apiOptions = {
  version: 'weekly',
}

const mapLoaderOptions = {
  apiKey: "AIzaSyC-xEY8-RgDdteff9etRy-SL4ehz5H6jbQ",
  divId: 'map',
  append: false,
  mapOptions: mapOptions,
  apiOptions: apiOptions
};

const mapLoader = new GoogleMap()

// async function loadMap() {
//   return await mapLoader.initMap(mapLoaderOptions)
// }

const initMap = () => {
  mapLoader.initMap(mapLoaderOptions)
  .then(googleMap => {

    const directionsRenderer = new google.maps.DirectionsRenderer();
    const directionsService = new google.maps.DirectionsService();
    directionsRenderer.setMap(googleMap)

    calculateAndDisplayRoute(directionsService, directionsRenderer)

  })
}

const calculateAndDisplayRoute = (directionsService, directionsRenderer) => {
  const request = {
      origin: new google.maps.LatLng(13.8058845, 100.535343),
      destination: new google.maps.LatLng(13.7466356, 100.5371464),
      travelMode: google.maps.TravelMode.DRIVING,
  }

  directionsService.route(request, (result, status) => {
      if (status === "OK") {
        directionsRenderer.setDirections(result);
      } else {
        window.alert("Directions request failed due to " + status);
      }
  })
}


class App extends React.Component {

    render() {

      initMap()

      return (
        <div id="map" style={{width: "100%", height: 500}}>
        </div>

      );
    }
}

export default App;
